/*Table structure for table `kbis_datasource` */

DROP TABLE IF EXISTS `kbis_datasource`;

CREATE TABLE `kbis_datasource` (
  `ID` varchar(32) NOT NULL COMMENT '编号',
  `S_NAME` varchar(60) DEFAULT NULL COMMENT '数据源名称',
  `S_TYPE` int(2) DEFAULT NULL COMMENT '数据源类型',
  `S_DESC` varchar(200) DEFAULT NULL COMMENT '源描述',
  `S_LOCAL` varchar(200) NOT NULL COMMENT '地址',
  `S_URL` varchar(200) DEFAULT NULL COMMENT '连接',
  `S_CONN` varchar(200) DEFAULT NULL COMMENT '连接用户',
  `S_PASS` varchar(32) DEFAULT NULL COMMENT '连接密码',
  `S_PROTOCOL` varchar(200) DEFAULT NULL COMMENT '协议',
  `CREATE_BY` varchar(32) DEFAULT NULL COMMENT '创建人',
  `CREATE_DATE` datetime DEFAULT NULL COMMENT '创建时间',
  `UPDATE_BY` varchar(32) DEFAULT NULL COMMENT '最近修改人',
  `UPDATE_DATE` datetime DEFAULT NULL COMMENT '最近修改时间',
  `DEL_FLAG` varchar(1) DEFAULT '0' COMMENT '逻辑删除标记(0.正常，1.删除)',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='数据源';

/*Data for the table `kbis_datasource` */

/*Table structure for table `kbis_page` */

DROP TABLE IF EXISTS `kbis_page`;

CREATE TABLE `kbis_page` (
  `ID` varchar(64) NOT NULL COMMENT '编号',
  `PAGE_NAME` varchar(200) DEFAULT NULL COMMENT '页面名称',
  `PAGE_DESC` varchar(1000) DEFAULT NULL COMMENT '页面描述',
  `PAGE_CONTENT` text COMMENT '页面内容',
  `PAGE_DATA` text COMMENT '数据绑定',
  `PAGE_URL` varchar(200) DEFAULT NULL COMMENT '页面地址',
  `PAGE_STOR` int(3) DEFAULT NULL COMMENT '页面排序',
  `CREATE_BY` varchar(32) DEFAULT NULL COMMENT '创建人',
  `CREATE_DATE` datetime DEFAULT NULL COMMENT '创建时间',
  `UPDATE_BY` varchar(32) DEFAULT NULL COMMENT '最近修改人',
  `UPDATE_DATE` datetime DEFAULT NULL COMMENT '最近修改时间',
  `DEL_FLAG` varchar(1) DEFAULT '0' COMMENT '逻辑删除标记(0.正常，1.删除)',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='分析页面表';

