mapper.select:
select, u.name u_name
, table1.PRO_NAME as PRO_ID_TEXT
from gen_create_table_history t
left join gen_projects table1 on t.PRO_ID = table1.ID
left join sys_user u on t.CREATE_BY = u.id
left join sys_organ o on u.organ_id = o.code

    1=1
    and t.ID = #{id}
    and t.PRO_ID = #{proId}
    and t.TABLE_NAME = #{tableName}
    and t.TABLE_COMMENTS = #{tableComments}
    and t.FIELD_NAME = #{fieldName}
    and t.FIELD_ALIAS = #{fieldAlias}
    and t.FIELD_TYPE = #{fieldType}
    and t.IS_PRIMARY = #{isPrimary}
    and t.FIELD_LENGTH = #{fieldLength}
    and t.FIELD_DECIMAL = #{fieldDecimal}
    and t.FIELD_DEFAULT_VALUE = #{fieldDefaultValue}
    and t.FIELD_IS_NULL = #{fieldIsNull}
    and t.SORT = #{sort}
    and t.CREATE_BY = #{createBy}
    and t.CREATE_DATE = #{createDate}
    and t.UPDATE_BY = #{updateBy}
    and t.UPDATE_DATE = #{updateDate}
    and t.DEL_FLAG = #{delFlag}
    and ${dynamicSQL}


    ORDER BY t.${sortC} ${order}

