mapper.delete:
delete from gen_create_table_history

    1=1
    and ID = #{id}
    and PRO_ID = #{proId}
    and TABLE_NAME = #{tableName}
    and TABLE_COMMENTS = #{tableComments}
    and FIELD_NAME = #{fieldName}
    and FIELD_ALIAS = #{fieldAlias}
    and FIELD_TYPE = #{fieldType}
    and IS_PRIMARY = #{isPrimary}
