mapper.select:
select

, table1.PRO_NAME as PRO_ID_TEXT
, F_CHANGE_TO_DICT('db_type', t.DB_TYPE) DB_TYPE_TEXT
from gen_db_manag t
left join GEN_PROJECTS table1 on t.PRO_ID = table1.ID

    1=1
    and t.ID = #{id}
    and t.PRO_ID = #{proId}
    and t.ALIAS like '%' || #{alias} || '%'
    and t.DB_TYPE = #{dbType}
    and t.DRIVER = #{driver}
    and t.DB_ADDRESS = #{dbAddress}
    and t.DB_PORT = #{dbPort}
    and t.TB_SCHEMA = #{tbSchema}
    and t.USER_NAME = #{userName}
    and t.PASSWORD = #{password}
    and t.CREATE_BY = #{createBy}
    and t.CREATE_DATE = #{createDate}
    and t.UPDATE_BY = #{updateBy}
    and t.UPDATE_DATE = #{updateDate}
    and t.DEL_FLAG = #{delFlag}
    and ${dynamicSQL}
    and t.PRO_ID in
        #{it}



    ORDER BY t.${sortC} ${order}

