mapper.select:
select, u.name u_name
, table1.PRO_NAME as PRO_ID_TEXT
, F_CHANGE_TO_DICT('coding_type', t.CODING) CODING_TEXT
from gen_pro_params t
left join gen_projects table1 on t.PRO_ID = table1.ID
left join sys_user u on t.CREATE_BY = u.id
left join sys_organ o on u.organ_id = o.code

    1=1
    and t.ID = #{id}
    and t.PRO_PATH = #{proPath}
    and t.SOURCE_FOLDER = #{sourceFolder}
    and t.HTML_PATH = #{htmlPath}
    and t.XML_PATH = #{xmlPath}
    and t.CODE_PATH = #{codePath}
    and t.JS_PATH = #{jsPath}
    and t.PRO_ID = #{proId}
    and t.ALIAS = #{alias}
    and t.AUTHOR = #{author}
    and t.CODING = #{coding}
    and t.CREATE_BY = #{createBy}
    and t.CREATE_DATE = #{createDate}
    and t.UPDATE_BY = #{updateBy}
    and t.UPDATE_DATE = #{updateDate}
    and t.DEL_FLAG = #{delFlag}
    and ${dynamicSQL}
    and t.PRO_ID in
        #{it}



    ORDER BY t.${sortC} ${order}

