mapper.delete:
delete from gen_pro_params

    1=1
    and ID = #{id}
    and PRO_PATH = #{proPath}
    and SOURCE_FOLDER = #{sourceFolder}
    and HTML_PATH = #{htmlPath}
    and XML_PATH = #{xmlPath}
    and CODE_PATH = #{codePath}
    and JS_PATH = #{jsPath}
    and PRO_ID = #{proId}
    and ALIAS = #{alias}
    and AUTHOR = #{author}
    and CODING = #{coding}
    and CREATE_BY = #{createBy}
    and CREATE_DATE = #{createDate}
    and UPDATE_BY = #{updateBy}
    and UPDATE_DATE = #{updateDate}
    and DEL_FLAG = #{delFlag}
