mapper.delete:
delete from gen_pro_team_member

    1=1
    and ID = #{id}
    and PRO_ID = #{proId}
    and MEMBER_ID = #{memberId}
    and POST = #{post}
    and DIVISION = #{division}
    and CREATE_BY = #{createBy}
    and CREATE_DATE = #{createDate}
    and UPDATE_BY = #{updateBy}
    and UPDATE_DATE = #{updateDate}
