mapper.delete:
delete from sys_dynamic_dict

    1=1
    and ID = #{id}
    and KEY_NAME = #{keyName}
    and VALUE_NAME = #{valueName}
    and SQL_CONTENT = #{sqlContent}
    and TYPE = #{type}
    and REMARKS = #{remarks}
    and DATA_SCOPE = #{dataScope}
    and TABLE_NAME = #{tableName}
    and SQL_MODE = #{sqlMode}
    and USER_ID_FIELD_BIND = #{userIdFieldBind}
    and ORGAN_CODE_FIELD_BIND = #{organCodeFieldBind}
