mapper.delete:
delete from demo_abnormal_rule

    1=1
    and RULE_CODE = #{ruleCode}
    and RULE_NAME = #{ruleName}
    and RULE_TYPE = #{ruleType}
    and RULE_PARAMTER = #{ruleParamter}
    and CREATE_DATE = #{createDate}
