mapper.select:
select, u.name u_name
from demo_data_imp_log t
left join sys_user u on t.CREATE_BY = u.id
left join sys_organ o on u.organ_id = o.code

    1=1
    and t.PKID = #{pkid}
    and t.DT_SOURCE = #{dtSource}
    and t.DT_COLLECTION = #{dtCollection}
    and t.DT_MODEL_CODE = #{dtModelCode}
    and t.DT_MODEL_NAME = #{dtModelName}
    and t.CREATE_DATE = #{createDate}
    and t.CREATE_BY = #{createBy}
    and t.DT_NUM = #{dtNum}
    and t.DESCRIPTION = #{description}
    and ${dynamicSQL}


    ORDER BY t.${sortC} ${order}

