mapper.select:
select

, table1.lable as S_TYPE_TEXT
from kbis_datasource t
left join (SELECT t.ALIAS LABLE, t.ID VALUE FROM GEN_DB_MANAG t ) table1 on t.S_TYPE = table1.value

    1=1
    and t.ID like CONCAT('%', #{id}, '%')
    and t.S_NAME like CONCAT('%', #{sName}, '%')
    and t.S_TYPE = #{sType}
    and t.S_DESC like CONCAT('%', #{sDesc}, '%')
    and t.S_LOCAL = #{sLocal}
    and t.S_URL = #{sUrl}
    and t.S_CONN = #{sConn}
    and t.S_PASS = #{sPass}
    and t.S_PROTOCOL = #{sProtocol}
    and t.CREATE_BY = #{createBy}
    and t.CREATE_DATE = #{createDate}
    and t.UPDATE_BY = #{updateBy}
    and t.UPDATE_DATE = #{updateDate}
    and t.DEL_FLAG = #{delFlag}
    and ${dynamicSQL}


    ORDER BY t.${sortC} ${order}

