mapper.delete:
delete from kbis_datasource

    1=1
    and ID = #{id}
    and S_NAME = #{sName}
    and S_TYPE = #{sType}
    and S_DESC = #{sDesc}
    and S_LOCAL = #{sLocal}
    and S_URL = #{sUrl}
    and S_CONN = #{sConn}
    and S_PASS = #{sPass}
    and S_PROTOCOL = #{sProtocol}
    and CREATE_BY = #{createBy}
    and CREATE_DATE = #{createDate}
    and UPDATE_BY = #{updateBy}
    and UPDATE_DATE = #{updateDate}
    and DEL_FLAG = #{delFlag}
