mapper.select:
select   
from kbis_page  t

	1=1
	and t.ID = #{id} 
	and t.PAGE_NAME like CONCAT('%', #{pageName}, '%')
	and t.PAGE_DESC like CONCAT('%', #{pageDesc}, '%')
	and t.PAGE_CONTENT = #{pageContent} 
	and t.PAGE_DATA = #{pageData} 
	and t.PAGE_URL like CONCAT('%', #{pageUrl}, '%')
	and t.PAGE_STOR like CONCAT('%', #{pageStor}, '%')
	and t.CREATE_BY = #{createBy} 
	and t.CREATE_DATE = #{createDate} 
	and t.UPDATE_BY = #{updateBy} 
	and t.UPDATE_DATE = #{updateDate} 
	and t.DEL_FLAG = #{delFlag} 
	 and ${dynamicSQL} 


	ORDER BY t.${sortC} ${order}

        select  
